import React, { useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';

function UploadPC() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [uploadedData, setUploadedData] = useState(null);
    const [showData, setShowData] = useState(false);

    const handleFileChange = (e) => {
        setSelectedFiles(e.target.files);
    };

    const handleUpload = async () => {
        if (selectedFiles.length === 0) {
            alert("Please select one or more CSV files to upload.");
            return;
        }

        Array.from(selectedFiles).forEach(file => {
            Papa.parse(file, {
                header: true,
                complete: async (results) => {
                    try {
                        const response = await axios.post('http://localhost:4300/pc-info', results.data, {
                            headers: {
                                'Content-Type': 'application/json'
                            }
                        });
                        console.log("Upload successful:", response.data);
                        alert("Upload successful!");
                        setUploadedData(response.data);
                        setShowData(true);
                    } catch (error) {
                        console.error("Upload error:", error);
                        alert("An error occurred while uploading. Please try again.");
                    }
                }
            });
        });
    };

    return (
        <div>
            <div className="input-group">
                <input
                    type="file"
                    className="form-control"
                    id="inputGroupFile04"
                    aria-describedby="inputGroupFileAddon04"
                    aria-label="Upload"
                    onChange={handleFileChange}
                />
                <button className="btn btn-danger" type="button" id="inputGroupFileAddon04" onClick={handleUpload}>
                    UPLOAD
                </button>
            </div>

            {showData && uploadedData && (
                <div>
                    <h2>Uploaded Data</h2>
                    <pre>{JSON.stringify(uploadedData, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default UploadPC;
