import React, { useState } from 'react';

function Pc() {
  const [pcData, setPcData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showTable, setShowTable] = useState(false); // State to toggle table visibility

  const fetchData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:4300/get-pc');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setPcData(data);
    } catch (error) {
      setError('Failed to fetch data: ' + error.message);
    }
    setIsLoading(false);
  };

  const toggleTable = () => {
    setShowTable(!showTable); // Toggle the visibility
  };

  return (
    <div>
      <button className='btn btn-primary w-100 ' onClick={fetchData} disabled={isLoading}>
        {isLoading ? 'Loading...' : 'Show all PC Data'}
      </button>
      <button className='btn btn-secondary ml-2' onClick={toggleTable}>
        {showTable ? 'Hide Table' : 'Show Table'}
      </button>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {showTable && (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>PC Name</th>
              <th>Start Time</th>
              <th>Last Time</th>
              <th>Total Time (s)</th>
              <th>School</th>
              <th>Lab Number</th>
              <th>PC Number</th>
            </tr>
          </thead>
          <tbody>
            {pcData.map(item => (
              <tr key={item._id}>
                <td>{item.pcname}</td>
                <td>{item.starttime}</td>
                <td>{item.lasttime}</td>
                <td>{item.totaltime}</td>
                <td>{item.schoolname}</td>
                <td>{item.labnum}</td>
                <td>{item.pcnum}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Pc;
