import React, { useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';

function UploadPC() {
    const [parsedData, setParsedData] = useState([]);

    const handleFileChange = (e) => {
        const file = e.target.files[0]; // Get the first file only for simplicity
        if (file) {
            Papa.parse(file, {
                header: true,
                skipEmptyLines: true,
                complete: function(results) {
                    setParsedData(results.data); // Set the parsed data to state
                    sendDatatoServer(results.data); // Send data to server
                }
            });
        }
    };

    const sendDatatoServer = (data) => {
        axios.post('http://localhost:4300/pc-info', data)
            .then(response => alert('Data uploaded successfully!'))
            .catch(error => alert('Failed to upload data: ' + error.message));
    };

    return (
        <div>
            <div className="input-group">
                <input
                    type="file"
                    accept=".csv"
                    className="form-control"
                    id="inputGroupFileAddon04"
                    aria-describedby="inputGroupFileAddon04"
                    aria-label="Upload"
                    onChange={handleFileChange}
                />
            </div>

            {parsedData.length > 0 && (
                <div>
                    <h2>Parsed CSV Data</h2>
                    <pre>{JSON.stringify(parsedData, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default UploadPC;
