import React, { useState, useEffect } from 'react';

function Schoolwisepc() {
  const [pcData, setPcData] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isTableVisible, setIsTableVisible] = useState(false);
  const baseUrl = process.env.REACT_APP_URL;

  useEffect(() => {
    // Fetch PC data initially
    fetchData();
  }, []);

  console.log(process.env.REACT_APP_URL);

  const fetchData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`${baseUrl}/get-pc`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setPcData(data);
    } catch (error) {
      setError('Failed to fetch data: ' + error.message);
    }
    setIsLoading(false);
  };

  const toggleTableVisibility = () => {
    if (!isTableVisible) {
      if (!selectedSchool) {
        setError('Please select a school.');
        return;
      }
    }
    setIsTableVisible(!isTableVisible);
  };

  const handleSchoolChange = (event) => {
    setSelectedSchool(event.target.value);
    setIsTableVisible(true); // Show the table when a school is selected
    setPcData([]); // Clear pcData when a new school is selected
    setError(null); // Reset error when a new school is selected
  };

  return (
    <div>
      <select value={selectedSchool} onChange={handleSchoolChange}>
        <option value="">Select a school...</option>
        {/* Option values will be generated based on unique school names in pcData */}
        {Array.from(new Set(pcData.map(item => item.schoolname))).map((schoolName, index) => (
          <option key={index} value={schoolName}>
            {schoolName}
          </option>
        ))}
      </select>
      <button className='btn btn-primary w-100' onClick={toggleTableVisibility} disabled={isLoading}>
        {isLoading ? 'Loading...' : isTableVisible ? 'Hide Table' : 'Show PC Data'}
      </button>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {isTableVisible && pcData.length > 0 && (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>PC Name</th>
              <th>Start Time</th>
              <th>Last Time</th>
              <th>Total Time (s)</th>
              <th>School</th>
              <th>Lab Number</th>
              <th>PC Number</th>
            </tr>
          </thead>
          <tbody>
            {pcData.map(item => (
              <tr key={item._id}>
                <td>{item.pcname}</td>
                <td>{item.starttime}</td>
                <td>{item.lasttime}</td>
                <td>{item.totaltime}</td>
                <td>{item.schoolname}</td>
                <td>{item.labnum}</td>
                <td>{item.pcnum}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Schoolwisepc;
