import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  const [view, setView] = useState('pc');  // Default view is 'pc'

  const handleLogout = () => {
    navigate('/login'); // Redirect to login page after logout
  };

  const handlePCClick = () => {
    setView('pc'); // Change view to "PC"
  };

  const handleVideoClick = () => {
    setView('video'); // Change view to "Video"
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/home">BrandName</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link className="nav-link active" aria-current="page" to="/home">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">About</Link>
              </li>
              <li className="nav-item">
                <button className="btn btn-link nav-link" onClick={handleLogout}>Logout</button>
              </li>
              <li className="nav-item">
                <button className="btn btn-link nav-link" onClick={handlePCClick}>PC</button>
              </li>
              <li className="nav-item">
                <button className="btn btn-link nav-link" onClick={handleVideoClick}>Video</button>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <div className="container mt-3">
        {view === 'pc' ? (
          <div className="alert alert-primary" role="alert">
            Hello
          </div>
        ) : (
          <div className="alert alert-secondary" role="alert">
            What's up
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
