import React, { useState, useEffect } from 'react';

function SchoolwisePC() {
    const [schoolData, setSchoolData] = useState([]);
    const [selectedSchool, setSelectedSchool] = useState(null);

    // Fetching data from the API using fetch
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:4300/get-pc');
                if (response.ok) {
                    const data = await response.json();
                    setSchoolData(data);
                } else {
                    throw new Error('Network response was not ok.');
                }
            } catch (error) {
                console.error('Error fetching data: ', error);
            }
        };

        fetchData();
    }, []);

    // Function to handle school selection
    const handleSelectSchool = (schoolName) => {
        const filteredData = schoolData.filter(data => data.schoolname === schoolName);
        setSelectedSchool(filteredData);
    };

    return (
        <div>
            <h1>School PC Usage Data</h1>
            <ul>
                {Array.from(new Set(schoolData.map(item => item.schoolname))).map(school => (
                    <li key={school} onClick={() => handleSelectSchool(school)}>
                        {school}
                    </li>
                ))}
            </ul>
            {selectedSchool && (
                <div>
                    <h2>Selected School: {selectedSchool.length > 0 ? selectedSchool[0].schoolname : ''}</h2>
                    {selectedSchool.map((item, index) => (
                        <div key={index}>
                            <p>PC Name: {item.pcname}</p>
                            <p>Total Time Used: {item.totaltime}</p>
                            <p>Last Used: {item.lasttime}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default SchoolwisePC;
