import React, { useState, useEffect } from 'react';

function SchoolwisePC() {
    const [schoolData, setSchoolData] = useState([]);
    const [selectedSchool, setSelectedSchool] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:4300/get-pc');
                if (response.ok) {
                    const data = await response.json();
                    setSchoolData(data);
                } else {
                    throw new Error('Network response was not ok.');
                }
            } catch (error) {
                console.error('Error fetching data: ', error);
            }
        };

        fetchData();
    }, []);

    const handleSelectSchool = (schoolName) => {
        const filteredData = schoolData.filter(data => data.schoolname === schoolName);
        setSelectedSchool(filteredData);
    };

    return (
        <div className="container mt-5">
            <ul className="list-group">
                {Array.from(new Set(schoolData.map(item => item.schoolname))).map(school => (
                    <li key={school} className="list-group-item list-group-item-action" onClick={() => handleSelectSchool(school)}>
                        {school}
                    </li>
                ))}
            </ul>
            {selectedSchool && selectedSchool.length > 0 && (
                <div className="mt-4">
                    <h4 className='text-center'>Details for {selectedSchool[0].schoolname}</h4>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>PC Name</th>
                                <th>PC Name</th>
                                <th>Start Time</th>
                                <th>Last Time</th>
                                <th>Total Time (s)</th>
                                <th>Lab Number</th>
                                <th>PC Number</th>
                                <th>Day ID</th>
                                <th>EIIN</th>
                            </tr>
                        </thead>
                        <tbody>
                            {selectedSchool.map((item, index) => (
                                <tr key={index}>
                                    <td>{index + 1}</td>
                                    <td>{item.pcname}</td>
                                    <td>{item.starttime}</td>
                                    <td>{item.lasttime}</td>
                                    <td>{item.totaltime}</td>
                                    <td>{item.labnum}</td>
                                    <td>{item.pcnum}</td>
                                    <td>{item.dayid}</td>
                                    <td>{item.eiin}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default SchoolwisePC;
